// This function now generates a plain text description without HTML.
function normalizeLinkedInJob(data) {
  const standardJob = {
    id: null, company_name: 'Unknown Company', job_title: 'Unknown Position',
    application_url: '', location: '', description: '', industry: 'Not specified',
    flexibility: 'Not specified', salary_range: 'Not specified', status: 'new',
    source: data.source || 'LinkedIn', created_at: new Date().toISOString()
  };

  // --- MAPPING LOGIC FOR LINKEDIN (unverändert) ---
  if (data.id) standardJob.id = String(data.id);
  if (data.postedDate) standardJob.created_at = data.postedDate;
  if (data.companyName) standardJob.company_name = data.companyName;
  if (data.title) standardJob.job_title = data.title;
  if (data.applyUrl || data.url) standardJob.application_url = data.applyUrl || data.url;
  if (data.location) standardJob.location = data.location;
  if (data.sector) standardJob.industry = data.sector;
  if (data.contractType) standardJob.flexibility = data.contractType;
  if (data.salary) standardJob.salary_range = data.salary;

  // --- Rule for Combined Description (PLAIN TEXT FORMAT) ---
  const descriptionParts = [];

  // 1. Main description text (we use the plain 'description' field)
  if (data.description) {
    descriptionParts.push(data.description);
  }

  // 2. Add other attributes as a plain text block
  const details = [];
  if (data.experienceLevel) {
    details.push(`Experience Level: ${data.experienceLevel}`);
  }
  if (data.workType) {
    details.push(`Work Type: ${data.workType}`);
  }
  if (data.sector) {
    details.push(`Sector: ${data.sector}`);
  }
  if (data.recruiterName) {
    details.push(`Recruiter: ${data.recruiterName}`);
  }

  if (details.length > 0) {
    // Add a text separator and join the details with newline characters
    descriptionParts.push("\n\n--- Job Details ---\n" + details.join('\n'));
  }

  // Join all parts into a single plain text string with double newlines
  standardJob.description = descriptionParts.join('');

  return standardJob;
}


