    }
  }
  // Join all parts into a single text block
  standardJob.description = descriptionParts.join('');


  // --- Mapping for other fields ---
  standardJob.job_title = inputData.title || standardJob.job_title;
  // Prioritize the direct jobUrl over the Indeed tracking url
  standardJob.application_url = inputData.jobUrl || inputData.url || standardJob.application_url;
