function normalizeJobData(inputData) {
  if (inputData && (inputData.employer || inputData.baseSalary)) {
    console.log("Detected Indeed (rich schema) format for job ID:", inputData.key);
    return normalizeIndeedJob(inputData);
  }
  if (inputData && (inputData.companyName || inputData.postedTimeAgo)) {
