  }

  if (details.length > 0) {
    // Add a text separator and join the details with newline characters
    descriptionParts.push("\n\n--- Job Details ---\n" + details.join('\n'));
  }

  // Join all parts into a single plain text string with double newlines
  standardJob.description = descriptionParts.join('');

  return standardJob;
}


// A dedicated function to parse the rich format from the Indeed scraper
function normalizeIndeedJob(data) {
    // This function remains the same as before to handle the Indeed format
    const standardJob = {
        id: null, company_name: 'Unknown Company', job_title: 'Unknown Position',
        application_url: '', location: '', description: '', industry: 'Not specified',
        flexibility: 'Not specified', salary_range: 'Not specified', status: 'new',
        source: data.source || 'Indeed', created_at: new Date().toISOString()
    };
    const raw_id = data.key || data.id;
