    if (data.datePublished) standardJob.created_at = data.datePublished;
    if (data.employer?.name) standardJob.company_name = data.employer.name;
    if (data.title) standardJob.job_title = data.title;
    if (data.jobUrl || data.url) standardJob.application_url = data.jobUrl || data.url;
    const loc = data.location;
    if (loc && typeof loc === 'object') {
        const city = loc.city || '', postalCode = loc.postalCode || '';
        if (postalCode && city) standardJob.location = `${postalCode} ${city}`;
        else standardJob.location = city || postalCode;
    } else if (typeof loc === 'string') {
        standardJob.location = loc;
    }
    const salary = data.baseSalary;
    if (salary && typeof salary === 'object') {
        const min = salary.min, max = salary.max, currency = salary.currencyCode || '', unit = salary.unitOfWork || 'period';
        if (min && max) standardJob.salary_range = `${currency} ${min} - ${max} per ${unit}`;
